import React, {Component} from 'react'

class Status extends Component {
  constructor(props) {
    super()
    this.state = {
      value: ''
    }
  }
  onAddStatus = (value) => {
    this.props.addStatus(value)
  }
  getValue = (e) => {
    this.setState({
      value: e.target.value
    })
  }
  render() {
    return (
      <>
        <input onKeyUp={this.getValue} name="status" placeholder="What do you need to do?"/>
        <button onClick={this.onAddStatus}>+</button>
      </>
    )
  }
}

export default Status
