import React from 'react'

class Item extends React.Component {

  render() {
    const { item, markCompleted,removeTodo} = this.props
    return (
      <li className={`${item.isCompleted ? 'completed' : ''}`}>
        <div className="box_list_item">
          <input 
            type="checkbox" 
            className="box_list_item_input" 
            checked={item.isCompleted}
            onChange={() => markCompleted(item.id)}
          />
          <label>{item.text}</label>
          <button className="destroy" onClick={() => removeTodo(item.id)}>x</button>
        </div>
      </li>
    )
  }
}

export default Item
