import React from 'react'
import './asset/main.scss'
import Students from './components/Students'


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      students: [
        {id: 1 , name: 'Nguyen Van A', score: 9.2 },
        {id: 2 , name: 'Nguyen Van B', score: 3.5 },
        {id: 3 , name: 'Nguyen Van C', score: 8.1 },
      ]
    }
  }
  getGreaterThan5 = () => {
    this.setState({
      students: this.state.students.filter(s => s.score > 5)
    })
  }
  render() {
    return (
      <div>
        <button onClick={this.getGreaterThan5}>Hien thi danh sach diem > 5 </button>
        <Students students={ this.state.students } />
      </div>
    )
  }
}

export default App
