import React from 'react'

class Header extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      text: ''
    }
  }
  render() {
    const { addItem } = this.props
    const text = this.state.text
    const setText = (text) => {
      this.setState({
        text
      })

    } 
    const onAddItem = (e = {}) => {
      addItem({
          id: new Date().valueOf(),
          text,
          isCompleted: true
        })
      setText('')
    }
    
    return (
      <div className="header">
        <h1 className="header_title">react todo app</h1>
        <input 
          className="header_input" 
          onChange={(e) => setText(e.target.value) }
          value={text}
        />
        <button onClick={(e) => onAddItem(e)}>+</button>
      </div>
    )
  }
}

export default Header
