import React from 'react'

class Footer extends React.Component {

    render() {
      const btns = [{
        title: 'All',
        isActive: true,
        onClick: () =>{},
        link: ''
      },
      {
        title: 'Active',
        isActive: false,
        onClick: () =>{},
        link: '/active'
      },
      {
        title: 'Completed',
        isActive: false,
        onClick: () =>{},
        link: '/completed'
      },
      ] 
      let elementBtns = btns.map((btn, index) => {
          return (
            <>
              <li key={index}>
                <a 
                  href={`#/${btn.link}`}
                  className={`${btn.isActived ? 'selected' : ''}`}
                  onClick={ btn.onClick }  >
                  {btn.title}
                </a>
              </li>
              <span></span>
            </>
          )
        } 
      )
      return (
          <div className="footer">
            <p>2 Items</p>
            <span className="footer_count"></span>
            <ul>
              {elementBtns}
            </ul>
          </div>
      )
    }
  }
  
  export default Footer
