import React from "react"
import "./asset/main.scss"
import Header from "./components/Header"
import ListItem from "./components/ListItem"
import Footer from "./components/Footer"
const filterByStatus = (listItem = [], status = '', id) => {
  switch (status) {
    case 'ACTIVE':
      return listItem.filter(item => !item.isCompleted)
    case 'COMPLETED':
      return listItem.filter(item => item.isCompleted)
    case 'REMOVE':
      return listItem.filter(item => item.id !== id)
    default:
      return listItem
  }
}

const filterTodosLeft = (listItem = []) => {
  return listItem.filter(item => !item.isCompleted)
}
class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      listItem: [],
      isCheckedAll: false,
      status: 'ALL',
      todoEditingId: ''
    }
  }
  addItem = (item = {}) => {
    const listItem = this.state.listItem
    listItem.push(item)
    this.setState({
      listItem      
    })
  }
  removeTodo = (id = '') => {
    this.setState(prevState => ({
      listItem: filterByStatus(prevState.listItem, 'REMOVE', id)
    }))
  }
  clearCompleted = () => {
    this.setState(preState => ({
      listItem: filterTodosLeft(preState.listItem)
    }))
  }
  render() {
    const { listItem, isCheckedAll, status} = this.state
    return (
      <div>
        <Header addItem={this.addItem} />
        <ListItem listItem = {listItem}
        />
        <Footer />
      </div>
    )
  }
}

export default App;
